export default `:root{--bg-color: #f9f9f9;--text-color: #222;--accent-color: #4e54c8;--subtitle-color: #555;--timer-bg: #fff;--timer-shadow: rgba(0, 0, 0, .05)}body{font-family:Poppins,Roboto,sans-serif;background:var(--bg-color);color:var(--text-color);display:flex;justify-content:center;align-items:center;height:100vh;margin:0;text-align:center}.container{max-width:800px;width:100%;padding:20px}.header{margin-bottom:40px}.header h1{font-size:3rem;font-weight:700;color:var(--accent-color);margin:0;line-height:1.2}.header p{font-size:1.1rem;color:var(--subtitle-color);margin:10px 0 0;line-height:1.4}.timer{display:flex;justify-content:center;gap:20px;flex-wrap:wrap}.time-segment{background:var(--timer-bg);box-shadow:0 2px 8px var(--timer-shadow);padding:15px 20px;min-width:80px;border-radius:8px}.time-segment div{font-size:3rem;font-weight:600;color:var(--accent-color)}.time-segment span{display:block;font-size:.85rem;color:var(--subtitle-color);margin-top:4px;text-transform:uppercase;letter-spacing:.5px}.expiry{font-size:.95rem;color:#777;margin-top:8px}
`;